import styles from './Select.module.css'


export default function Select({text, name, options, handleOnChange, value}){
    return(
        <div className={styles.form_control}>
            <select className={styles.select} name={name} id={name} onChange={handleOnChange}>
                  
                  {options.length > 0 ? (
                      options.map((options) =>(
                    <option value={options.id} key={options.id}>{options.valor}</option>
                ))): 
                <p>Sem amigos</p>
            }} 
            </select>
            <label className={styles.label}  htmlFor={name}>{text}</label>
        </div>
    )
}
