import Loading from '../layout/elements/Loading'
import Container from '../layout/Container'

import {useParams} from 'react-router-dom'
import { useState, useEffect} from 'react'

export default function Candidato(){
   const { id} = useParams()

   const [candidato, setCandidatato] = useState([])
    const [showCandidato, setShowCandidato] = useState(false)
   useEffect(() => {
    setTimeout(() => {
        fetch(`http://localhost:5000/candidatos/${id}`,{
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        }).then((response) => response.json()).then((response) =>{
            setCandidatato(response)
        }).catch((error) => console.log(error))
    }, 5000)
   }, [id])

   function toggleCandidatatoForm(){
        setShowCandidato(!showCandidato)
   }

    return (
        <>
        {candidato.name ? (
            <div>
                <Container customClass="column">
                    <div>
                        <h1>Nome: {candidato.name}</h1>

                        <button onClick={toggleCandidatatoForm}>{!showCandidato ? 'Editar' : 'Fechar'}</button>
                        {!showCandidato ? (
                            <div>
                                <table className="table table-striped">
            <thead>
                <tr>
                <th scope="col">#</th>
                <th scope="col">Nome</th>
                <th scope="col">Idade</th>
                <th scope="col">Sexo</th>
                <th scope="col">E-mail</th>
                <th scope="col">Telefone</th>
                <th scope="col">Vaga candidatada</th>
                </tr>
            </thead>
            <tbody>
                {candidato && candidato.map((candidato) =>(
                <tr key={candidato.id}>          
                <th scope="row">{candidato.id}</th>
                <td>{candidato.name}</td>
                <td>{candidato.idade}</td>
                <td>{candidato.sexo.name}</td>
                <td>{candidato.email}</td>
                <td>{candidato.telefone}</td>
                <td>{candidato.vagaCandidatada.name}</td>
                </tr>
                    ))}
                </tbody>
            </table>
                            </div>
                        ) : (
                            <div>Detalhes</div>
                        )}
                    </div>
                </Container>
            </div>
        ) : (
            <Loading />
        )}
        </>
    )
}
